import React, { useState } from 'react';
import { createRule } from '../api/rules';

type Props = {
    tenantId: string;
    onSuccess: () => void;
};

export const AddRuleForm: React.FC<Props> = ({ tenantId, onSuccess }) => {
    const [ruleIndex, setRuleIndex] = useState<number>(0);
    const [action, setAction] = useState<'Allow' | 'Block'>('Allow');
    const [sources, setSources] = useState([{ name: '', email: '' }]);
    const [destinations, setDestinations] = useState([{ name: '', address: '' }]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        await createRule({
            tenantId,
            ruleIndex,
            action,
            source: sources,
            destination: destinations,
        });

        onSuccess(); // נרענן את הרשימה
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-4 p-4 border rounded">
            <div>
                <label className="block">Rule Index</label>
                <input type="number" value={ruleIndex} onChange={(e) => setRuleIndex(Number(e.target.value))} className="border p-1 w-full" />
            </div>

            <div>
                <label className="block">Action</label>
                <select value={action} onChange={(e) => setAction(e.target.value as 'Allow' | 'Block')} className="border p-1 w-full">
                    <option value="Allow">Allow</option>
                    <option value="Block">Block</option>
                </select>
            </div>

            <div>
                <label className="block">Sources</label>
                {sources.map((s, idx) => (
                    <div key={idx} className="flex gap-2 mb-1">
                        <input
                            type="text"
                            placeholder="Name"
                            value={s.name}
                            onChange={(e) => {
                                const copy = [...sources];
                                copy[idx].name = e.target.value;
                                setSources(copy);
                            }}
                            className="border p-1 flex-1"
                        />
                        <input
                            type="email"
                            placeholder="Email"
                            value={s.email}
                            onChange={(e) => {
                                const copy = [...sources];
                                copy[idx].email = e.target.value;
                                setSources(copy);
                            }}
                            className="border p-1 flex-1"
                        />
                    </div>
                ))}
            </div>

            <div>
                <label className="block">Destinations</label>
                {destinations.map((d, idx) => (
                    <div key={idx} className="flex gap-2 mb-1">
                        <input
                            type="text"
                            placeholder="Name"
                            value={d.name}
                            onChange={(e) => {
                                const copy = [...destinations];
                                copy[idx].name = e.target.value;
                                setDestinations(copy);
                            }}
                            className="border p-1 flex-1"
                        />
                        <input
                            type="text"
                            placeholder="Address"
                            value={d.address}
                            onChange={(e) => {
                                const copy = [...destinations];
                                copy[idx].address = e.target.value;
                                setDestinations(copy);
                            }}
                            className="border p-1 flex-1"
                        />
                    </div>
                ))}
            </div>

            <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
                Add Rule
            </button>
        </form>
    );
};
