import React, { useEffect, useState } from 'react';
import axios from "axios";

type Rule = {
    _id: string;
    ruleIndex: number;
    action: string;
    source: string;
    destination: string;
};

type Props = {
    tenantId: string;
};

export const RuleTable: React.FC<Props> = ({ tenantId }) => {
    const [rules, setRules] = useState<Rule[]>([]);
    const [page, setPage] = useState(1);
    const [total, setTotal] = useState(0);
    const limit = 10;

    useEffect(() => {
        axios
            .get(`/rules/${tenantId}?page=${page}&limit=${limit}`)
            .then((res) => {
                setRules(res.data.data);
                setTotal(res.data.total);
            })
            .catch((err) => {
                console.error('Error fetching rules:', err);
            });
    }, [tenantId, page]);

    const totalPages = Math.ceil(total / limit);

    return (
        <div className="p-4">
            <h2 className="text-xl font-bold mb-4">Rule Table</h2>
            <table className="min-w-full border">
                <thead>
                <tr className="bg-gray-100 text-left">
                    <th className="p-2 border">#</th>
                    <th className="p-2 border">Action</th>
                    <th className="p-2 border">Source</th>
                    <th className="p-2 border">Destination</th>
                </tr>
                </thead>
                <tbody>
                {rules.map((rule, index) => (
                    <tr key={rule._id} className="border-t">
                        <td className="p-2 border">{rule.ruleIndex}</td>
                        <td className="p-2 border">{rule.action}</td>
                        <td className="p-2 border">{rule.source}</td>
                        <td className="p-2 border">{rule.destination}</td>
                    </tr>
                ))}
                </tbody>
            </table>

            {/* Pagination */}
            <div className="mt-4 flex justify-center gap-2">
                <button
                    onClick={() => setPage((p) => Math.max(1, p - 1))}
                    disabled={page === 1}
                    className="px-3 py-1 bg-gray-200 rounded disabled:opacity-50"
                >
                    Prev
                </button>
                <span className="px-2 py-1">{page} / {totalPages}</span>
                <button
                    onClick={() => setPage((p) => Math.min(totalPages, p + 1))}
                    disabled={page === totalPages}
                    className="px-3 py-1 bg-gray-200 rounded disabled:opacity-50"
                >
                    Next
                </button>
            </div>
        </div>
    );
};
