import { api } from './axios';

export const getRules = async (tenantId: string, page: number, limit: number = 10) => {
  const response = await api.get(`/rules/${tenantId}?page=${page}&limit=${limit}`);
  return response.data;
};

export const createRule = async (rule: any) => {
  const res = await api.post('/rules', rule);
  return res.data;
};
